import React, { useState } from "react";

function AddTransactionForm() {
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [amount, setAmount] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  function handleSubmit(e) {
    e.preventDefault();
    setSubmitting(true);
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        date: date,
        description: description,
        category: category,
        amount: amount,
      }),
    })
    .then(response => {
      if (response.ok) {
        setSuccess(true);
        setDate("");
        setDescription("");
        setCategory("");
        setAmount("");
      } else {
        throw new Error("Failed to add transaction");
      }
    })
    .catch(error => {
      setError(error.message);
    })
    .finally(() => {
      setSubmitting(false);
    });
  }

  return (
    <div className="ui segment">
      <form onSubmit={handleSubmit} className="ui form">
        <div className="inline fields">
          <input
            value={date}
            onChange={(e) => setDate(e.target.value)}
            type="date"
            name="date"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            type="text"
            name="description"
            placeholder="Description"
          />
          <input
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            type="text"
            name="category"
            placeholder="Category"
          />
          <input
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            type="number"
            name="amount"
            placeholder="Amount"
            step="0.01"
          />
        </div>
        <button className="ui button" type="submit" disabled={submitting}>
          {submitting ? "Adding..." : "Add Transaction"}
        </button>
      </form>
      {success && <p className="success-message">Transaction added successfully!</p>}
      {error && <p className="error-message">Failed to add transaction. Please try again.</p>}
    </div>
  );
}

export default AddTransactionForm;
